import { Component, useState } from "react";
import { TouchableOpacity,Button, Image, StyleSheet, Text, View, TextInput } from "react-native";
import DatePicker from "react-datepicker";
import DateTimePicker from '@react-native-community/datetimepicker';

/**
 * @description This class is used to display the log in form
 */
function SignUp(){

    const [show, setShow]=useState(false);
    const [date, setDate]=useState(new Date(2000,0,1));

    //Actual date of birth to be sent
    const [dob, setDob]=useState();
    const onChnage=(e, selectedDate)=>{
        const currentDate=selectedDate || date;
        setShow(false);
        setDate(currentDate);
        setDob(currentDate);
    }

    const showDatePicker=()=>{
        setShow(true);
    }

    /**
       * @description render() returns a div 
       * @returns The div containing 
       */
    
        return (
            <View>
                {show && (
                    <DateTimePicker
                        testID="dateTimePicker"
                        value={date}
                        mode='date'
                        is24Hour={true}
                        display="default"
                        onChange={onChange}/>
                )}
                <TouchableOpacity onClick={showDatePicker}>
                <TextInput
                    placeholder="Date Of Birth"
                    className="form-input form-input-placeholder" 
                    isDate={true}
                    editable={false}
                    onClick={showDatePicker}
                    />
                </TouchableOpacity>
                <TextInput
                    placeholder="Display Name*"
                    className="form-input form-input-placeholder" />
                <TextInput
                    placeholder="Email*"
                    className="form-input" />
                <TextInput
                    placeholder="Password"
                    className="form-input" />
                <Text className="error" textWrap="true"></Text>
                <Button className="button-signup" title="Sign In"></Button>
                <Button className="button-signup" title="Forgot Password"></Button>
            </View>
        )
    
}

export default SignUp;
