import { Component } from "react";
import { TouchableOpacity, Pressable, Button, Image, StyleSheet, Text, View, TextInput } from "react-native";

// import authenticationStyles from "../styles.js";

/**
 * @description This class is used to display the log in form
 */
class LogIn extends Component {

    constructor() {
        super();
        this.state = {
        }
    }

    /**
       * @description render() returns a div 
       * @returns The div containing 
       */
    render() {
        return (
            <View>
               
                <TextInput
                    style={styles.formInput}
                    placeholder="Email"
                    placeholderTextColor='white'
                    className="form-input" 
                    />
               
                <TextInput
                    style={styles.formInput}
                    placeholder="Password"
                    placeholderTextColor='white'
                    className="form-input" 
                    secureTextEntry={true}/>
                    

                <Text style={styles.error} class="error" textWrap="true" >fdgdf</Text>

                <TouchableOpacity style={styles.buttonSignIn}>
                    <Text style={styles.textSignIn}>Sign In</Text>
                </TouchableOpacity>

                <TouchableOpacity style={styles.buttonForgotPass}>
                    <Text style={styles.textForgotPass}>Forgot Password</Text>
                </TouchableOpacity>

            </View>
        )
    }
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
      },
      
    textForgotPass: {
        fontSize: 19,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'darkviolet',
    },
    buttonForgotPass: {
        alignItems: "center",
        justifyContent: "center",
        borderColor: 'darkviolet',
        borderWidth: 1,
        backgroundColor: 'white',
        fontWeight: 'bold',
        borderRadius: 100,
        marginBottom: 10,
        minHeight: 44,
        height: 44,
    },

    textSignIn: {
        color: 'white',
        fontWeight: 'bold',
        fontSize: 19,
    },

    buttonSignIn: {
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: 'violet',
        borderRadius: 100,
        padding: 5,
        marginBottom: 10,
    },

    error: {
        color: 'red',
        fontSize: 20,
        marginBottom:5,
    },

    formInput:{
        fontSize: 20,
        marginBottom: 5,
        borderBottomWidth:1,
        borderColor:'white',
        paddingBottom:5,
    },

});

export default LogIn;
